import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  validAge:boolean = true;
  validName:boolean = true;
  validEmail:boolean = true;

  formValidation = new FormGroup({
    name:new FormControl("",[Validators.required,Validators.minLength(3)]),
    age:new FormControl(null,[Validators.required,Validators.min(20),Validators.max(40)]),
    email:new FormControl("",[Validators.required,Validators.email])
  }
  )
  submit(){
    this.validAge = this.formValidation.controls.age.valid;
    this.validName = this.formValidation.controls.name.valid;
    this.validName = this.formValidation.controls.email.valid;

    console.log(this.formValidation.controls.age.valid);
  }
}
<div class="container mt-5">
  <div class="row justify-content-center p-3 mb-5">
    <div class="col-md-6">
      <div class="card">
        <div class="card-header bg-primary text-white text-center">Registration Form</div>
        <div class="card-body p-3 m-1 ">
          <form (ngSubmit)="submit()" [formGroup] = "formValidation">
            <div class="form-group p-3 m-1">
              <div class="text-center">
                <label>Name</label>
              </div>
              <input formControlName="name" type="text" name="name" class="form-control" required="" placeholder="Name">
            </div>
            <div class="form-group p-3 m-1">
              <div class="text-center">
                <label>Age</label>
              </div>
              <input formControlName="age" type="number" name="age" class="form-control" required="" placeholder="Age">
            </div>
            <div class="form-group p-3 m-1">
              <div class="text-center">
                <label>Email</label>
              </div>
              <input formControlName="email" type="email" name="email" class="form-control" required="" placeholder="Email">
            </div>
            <div class = "text-center">
              <button type="submit" class="btn btn-primary ">Submit</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
